import { ICancelAndHoldAutomationEvent } from 'automation-events/src/interfaces';
import { TAutomationEvent } from 'automation-events/src/types';

export const isCancelAndHoldAutomationEvent = (automationEvent: TAutomationEvent): automationEvent is ICancelAndHoldAutomationEvent => {
    return automationEvent.type === 'cancelAndHold';
};
